import React, { Component, useState } from 'react';
import { Link } from 'react-router-dom';

import Header from '../../components/Header';
import { ButtonGroup, Button, Modal, ModalHeader, ModalBody, ModalFooter, Input, Label, Form, FormGroup, Table } from 'reactstrap';



export default class Dashboard extends Component {

    constructor() {
        super();
        this.state = {
            clients: [],
        }
    }

    componentDidMount() {

        const token = localStorage.getItem('token');
        fetch('http://localhost:3333/clients', { headers: new Headers({ 'Authorization': `Bearer ${token}` })})
        .then(response => {
            if(response.ok) {
                return response.json();
            }
            throw new Error("Oops! Ocurrio un error. :(");
        })
        .then(clients => this.setState({ clients })
        )
        .catch(e => console.log(e));
    }

    render() {
      const ModalExample = (props) => {
const {
  buttonLabel,
  className
} = props;
const [modal, setModal] = useState(false);
const [backdrop, setBackdrop] = useState(true);
const [keyboard, setKeyboard] = useState(true);

const toggle = () => setModal(!modal);

const changeBackdrop = e => {
  let value = e.target.value;
  if (value !== 'static') {
    value = JSON.parse(value);
  }
  setBackdrop(value);
}

const changeKeyboard = e => {
  setKeyboard(e.currentTarget.checked);
}
}
        return (
            <div className="col-md-8">
                <Header title="Clientes" />
                <hr className="my-3" />
                <p>
                <Table>
                  <thead>
                    <tr>
                      <th>#</th>
                      <th>Nombre</th>
                      <th>Email</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                  {this.state.clients.map((user =>
                    <tr key={user.id}>
                      <td>{user.id}</td>
                      <td>{user.username}</td>
                      <td>{user.email}</td>
                      <td>
                      <ButtonGroup>
                        <Button color="primary" size="sm">Detalles</Button>
                        <Button color="warning" size="sm">Editar</Button>
                        <Button color="danger" size="sm">Eliminar</Button>
                      </ButtonGroup>
                      </td>
                    </tr>
                    ))}
                  </tbody>
                </Table>
                </p>
                <div className="text-center" style={{marginBottom: 150}}>
                    <Link to="/logout" className="btn btn-outline-primary"> Cerrar Sesión </Link>
                </div>

            </div>
        );
    }
}
