import React, { Component } from 'react';
import { Link } from 'react-router-dom';

import Header from '../../components/Header';

export default class Dashboard extends Component {

    constructor() {
        super();
        this.state = {
            clients: {},
        }
    }

    componentDidMount() {
        const token = localStorage.getItem('token');
        fetch('http://localhost:3333/clients', { headers: new Headers({ 'Authorization': `Bearer ${token}` })})
        .then(response => {
            if(response.ok) {
                return response.json();
            }
            throw new Error("Oops! Ocurrio un error. :(");
        })
        .then(clients => this.setState({ clients }))
        .catch(e => console.log(e));
    }

    render() {
        return (
            <div>
                <Header title="Dashboard" />
                <hr className="my-3" />
                <p>
                    <code> {this.state.clients} ha iniciado sesi√≥n correctamente ! ^-^  </code>
                </p>
                <div className="text-center">
                    <Link to="/logout" className="btn btn-outline-primary"> Log Out </Link>
                </div>
            </div>
        );
    }
}
